SELECT Results.job_processing_uid AS `Results__job_processing_uid`, Results.test_type_uid AS `Results__test_type_uid`, Results.test_counter AS `Results__test_counter`, Results.number AS `Results__number`, Results.country AS `Results__country`, Results.start_time AS `Results__start_time`, Results.end_time AS `Results__end_time`, Results.connect_time AS `Results__connect_time`, Results.score AS `Results__score`, Results.url AS `Results__url`, Results.added_by AS `Results__added_by`, Results.added_on AS `Results__added_on`, Results.status AS `Results__status`, Users.id AS `Users__id`, Users.name AS `Users__name`, Users.email AS `Users__email`, Users.admin AS `Users__admin`, Users.password AS `Users__password`, Users.status AS `Users__status`, Users.created_by AS `Users__created_by`, Users.created_on AS `Users__created_on` FROM results Results INNER JOIN users Users ON Users.id = (Results.added_by) WHERE status = :c0 LIMIT 20 OFFSET 0